import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import OutlinedInput from '@mui/material/OutlinedInput';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

    const diets = [
        'balanced',
        'high-fiber',
        'high-protein',
        'low-carb',
        'low-fat',
        'low-sodium',
      ];

    const health = [
        'alcohol-cocktail',
        'alcohol-free',
        'celery-free',
        'crustacean-free',
        'dairy-free',
        'egg-free',
        'fish-free',
        'fodmap',
        'gluten-free',
        'lupine-free',
        'mollusk-free',
        'mustard-free',
        'no-oil-Added',
        'peanut-free',
        'vegan',
        'vegetarian',
        'wheat-free',
 ]

 const cuisineType =[
    'American',
    'Asian',
    'British',
    'Caribbean',
    'Central Europe',
    'Chinese',
    'Eastern Europe',
    'French',
    'Indian',
    'Italian',
    'Japanese',
    'Kosher',
    'Mediterranean',
     'Mexican',
     'Middle Eastern',
     'Nordic',
     'South American',
     'South East Asian',
 ]

 const mealType= [
    'Breakfast',
    'Dinner',
    'Lunch',
    'Snack',
    'Teatime',

 ]

 const dishType = [
    'Side dish',
    'Soup',
    'Starter',
    'Sweets',
    'Biscuits and cookies',
    'Bread',
    'Cereals',
    'Condiments and sauces',
    'Desserts',
    'Drinks',
    'Main Course',
    'Pancake',
   ' Preps',
    'Preserve',
    'Salad',
    'Sandwiches'

 ]

 function getStyles(health, healthTypes, theme) {
    return {
      fontWeight:
        healthTypes.indexOf(health) === -1
          ? theme.typography.fontWeightRegular
          : theme.typography.fontWeightMedium,
    };
  }
    
    const ReceipeSearch = () => {
        const theme = useTheme();
        const [healthTypes, sethealthTypes] = useState([]);
        const [Diets, setDiets] = useState([]);
        const [meal, setMeal] = useState('');
        const [dish, setDish] = useState('');
        const [cuisine, setCuisine] = useState('');
      
        const handleChangehealth = (event) => {
          const {
            target: { value },
          } = event;
          sethealthTypes(
            // On autofill we get a stringified value.
            typeof value === 'string' ? value.split(',') : value,
          );
        };
        const handleChangediets = (event) => {
            const {
              target: { value },
            } = event;
            setDiets(
              // On autofill we get a stringified value.
              typeof value === 'string' ? value.split(',') : value,
            );
          };
          const handleChangemeal = (event) => {
            const {
              target: { value },
            } = event;
            setMeal(
              // On autofill we get a stringified value.
              value
            );
          };
          const handleChangeDish = (event) => {
            const {
              target: { value },
            } = event;
            setDish(
              // On autofill we get a stringified value.
              value
            );
          };
          const handleCusine = (event) => {
            const {
              target: { value },
            } = event;
            setCuisine(
              // On autofill we get a stringified value.
             value
            );
          };
      
      return (
        <div>
      <FormControl sx={{ m: 1, width: 300, mt: 3 }}>
        <Select
          multiple
          displayEmpty
          value={healthTypes}
          onChange={handleChangehealth}
          input={<OutlinedInput />}
          renderValue={(selected) => {
            if (selected.length === 0) {
              return <em>Allergies/healthTypes</em>;
            }

            return selected.join(', ');
          }}
          MenuProps={MenuProps}
          inputProps={{ 'aria-label': 'Without label' }}
        >
          <MenuItem disabled value="">
            <em>Allergies/healthTypes</em>
          </MenuItem>
          {health.map((health) => (
            <MenuItem
              key={health}
              value={health}
              style={getStyles(health, healthTypes, theme)}
            >
              {health}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    </div>
      )
    }
    
    export default ReceipeSearch
